import React, { useEffect, useState } from "react";
import "./business-segments-tabs.css";
import axios from "axios";
import Api from "../../Api/Api";
import {
  BsFillArrowRightCircleFill,
  BsFillArrowLeftCircleFill,
} from "react-icons/bs";

function Businesssegmentstabs() {
  const [salesData, setSalesData] = useState([]);
  const [costOfSalesData, setCostOfSalesData] = useState([]);
  const [netIncomeData, setNetIncomeData] = useState([]);
  const [totalAssetsData, setTotalAssetsData] = useState([]);
  const [totalLiabilitiesData, setTotalLiabilitiesData] = useState([]);
  const [currency, setCurrency] = useState("Riyal");
  const [fiscalPeriodType, setFiscalPeriodType] = useState("year");

  useEffect(() => {
    Api.getAccessToken();
    axios
      .get(
        `https://Data.argaam.com/api/v1/json/ir-api/financial-statements/en?fiscalPeriodType=${fiscalPeriodType}`,
        {
          headers: { authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )
      .then((response) => {
        setSalesData(response.data.tabs[0].fields[0].values);
        setCostOfSalesData(response.data.tabs[0].fields[1].values);
        setNetIncomeData(response.data.tabs[0].fields[23].values);
        setTotalAssetsData(response.data.tabs[1].fields[36].values);
        setTotalLiabilitiesData(response.data.tabs[1].fields[48].values);

        console.log(response.data);
      });
  }, [fiscalPeriodType]);
  return (
    <div className="business-segments container">
      <div className="header-of-tabs">
        <div className="tabs-container">
          <span className="tabs">
            {" "}
            <a className="btn btn-light" onClick={setFiscalPeriodType("year")}>
              ANNUAL
            </a>
          </span>
          <span className="tabs">
            {" "}
            <a
              className="btn btn-light"
              onClick={setFiscalPeriodType("quarter")}
            >
              QUARTER
            </a>
          </span>
          <span className="tabs">
            {" "}
            <a
              className="btn btn-light"
              onClick={setFiscalPeriodType("quarter")}
            >
              INTERIM
            </a>
          </span>
        </div>

        <div className="currency-container text-end">
          <div className="currency">
            <span>Currency : </span>

            <span>
              <a
                className="btn btn-light"
                id="riyal-btn"
                onClick={() => {
                  document.getElementById("riyal-btn").classList.add("active");
                  document.getElementById("usd-btn").classList.remove("active");
                  setCurrency("RIYAL");
                }}
              >
                RIYAL
              </a>
            </span>
            <span>
              <button
                class="btn btn-light"
                id="usd-btn"
                onClick={() => {
                  document.getElementById("usd-btn").classList.add("active");
                  document
                    .getElementById("riyal-btn")
                    .classList.remove("active");
                  setCurrency("USD");
                }}
              >
                USD
              </button>
            </span>
          </div>

          <div className="my-5">
            <table className="center" style={{ width: "100%" }}>
              <tbody>
                <tr style={{ backgroundColor: "white", height: "100px" }}></tr>
                <tr
                  style={{ backgroundColor: "#f5f5f5", height: "100px" }}
                ></tr>
                <tr>
                  <td colSpan={5}>
                    <BsFillArrowLeftCircleFill />
                  </td>
                  {salesData.map((v) => {
                    return (
                      <td
                        style={{
                          backgroundColor: "white",
                          fontSize: "12px",
                          padding: "15px",
                        }}
                        colSpan={3}
                      >
                        {v.fiscalPeriod}
                      </td>
                    );
                  })}
                  <td>
                    <BsFillArrowRightCircleFill />
                  </td>
                </tr>
                <tr>
                  <td colSpan={5}></td>
                  {salesData.map((v) => {
                    return (
                      <td colSpan={3} style={{ fontSize: "12px" }}>
                        Million {currency}
                      </td>
                    );
                  })}
                  <td></td>
                </tr>

                <tr style={{ backgroundColor: "#f5f5f5", height: "50px" }}></tr>

                <tr>
                  <td style={{ fontSize: "15px" }} colSpan={5}>
                    Sales Income
                  </td>

                  {salesData.map((v) => {
                    if (currency == "RIYAL") {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {v.value.toFixed(2)}
                        </td>
                      );
                    } else {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {(v.value * 0.27).toFixed(2)}
                        </td>
                      );
                    }
                  })}
                </tr>
                <tr style={{ backgroundColor: "#f5f5f5", height: "50px" }}></tr>

                <tr>
                  <td style={{ fontSize: "15px" }} colSpan={5}>
                    CostOfSalesData{" "}
                  </td>

                  {costOfSalesData.map((v) => {
                    if (currency == "RIYAL") {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {v.value.toFixed(2)}
                        </td>
                      );
                    } else {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {(v.value * 0.27).toFixed(2)}
                        </td>
                      );
                    }
                  })}
                </tr>

                <tr style={{ backgroundColor: "#f5f5f5", height: "50px" }}></tr>

                <tr>
                  <td style={{ fontSize: "15px" }} colSpan={5}>
                    netIncomeData{" "}
                  </td>

                  {netIncomeData.map((v) => {
                    if (currency == "RIYAL") {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {v.value.toFixed(2)}
                        </td>
                      );
                    } else {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {(v.value * 0.27).toFixed(2)}
                        </td>
                      );
                    }
                  })}
                </tr>

                <tr style={{ backgroundColor: "#f5f5f5", height: "50px" }}></tr>

                <tr>
                  <td style={{ fontSize: "15px" }} colSpan={5}>
                    totalAssetsData{" "}
                  </td>

                  {totalAssetsData.map((v) => {
                    if (currency == "RIYAL") {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {v.value.toFixed(2)}
                        </td>
                      );
                    } else {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {(v.value * 0.27).toFixed(2)}
                        </td>
                      );
                    }
                  })}
                </tr>

                <tr style={{ backgroundColor: "#f5f5f5", height: "50px" }}></tr>

                <tr>
                  <td style={{ fontSize: "15px" }} colSpan={5}>
                    totalLiabilitiesData{" "}
                  </td>

                  {totalLiabilitiesData.map((v) => {
                    if (currency == "RIYAL") {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {v.value.toFixed(2)}
                        </td>
                      );
                    } else {
                      return (
                        <td colspan={3} style={{ fontSize: "12px" }}>
                          {(v.value * 0.27).toFixed(2)}
                        </td>
                      );
                    }
                  })}
