import React, { useEffect } from "react";
import "./Investorrepresentation.css";

function Investorrepresentation() {
  useEffect(() => {
    axios
      .get(
        `https://data.argaam.com/api/v1.0/json/ir-api/business-segments?fiscalPeriodType=${fiscalPeriodType}&currency=${currency}`,
        {
          headers: {
            authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      )
      .then((response) => {
        setSalesData(response.data.fsFields[0]);
        setCostOfSalesData(response.data.fsFields[1]);
        setNetIncomeData(response.data.fsFields[2]);
        setTotalAssetsData(response.data.fsFields[3]);
        setTotalLiabilitiesData(response.data.fsFields[4]);
        console.log(response.data);
        setTimeout(() => {
          setLoading(true);
        }, 1000);
      });
  }, []);
  return (
    <div className="investor-representation">
      <div className="container-fluid">
        <div className="investors-presentation-data-header">
          <form className="htd__form">
            <div className="type d-flex flex-row justify-content-between">
              <p className="my-auto">Type</p>
              <div className="select-dropdown">
                <select>
                  <option value="1">Earnings Call</option>
                  <option value="2">Annual Report </option>
                  <option value="3">Corporate Guides</option>
                  <option value="4">Minutes Of General</option>
                  <option value="5">Articles Of general </option>
                  <option value="6"> Others</option>
                  <option value="7"> Real Estate Valuation Report</option>
                  <option value="8">Investor Presentations </option>
                  <option value="9"> Earnings Call Script</option>
                </select>
              </div>
            </div>

            <div className="date d-flex flex-row  justify-content-between">
              <p className="my-auto">Date From</p>

              <input type="date" />

              <p className="my-auto">Date To</p>

              <input type="date" />
            </div>
          </form>
        </div>

        <div className="reports-table w-100">
          <table className="w-100">
            <tr>
              <td>Date</td>
              <td>Report</td>
              <td>Report Type</td>
              <td>Downloads</td>
            </tr>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Investorrepresentation;
