import React, { useEffect, useState } from "react";
import "./Investorrepresentation.css";
import axios from "axios";

function Investorrepresentation() {
  const [reportsTable, setReportsTable] = useState([]);

  useEffect(() => {
    axios
      .get(
        `https://data.argaam.com/api/v1.0/json/ir-api/investors-presentation?reportTypeID=&dateFrom=&dateTo=`,
        {
          headers: {
            authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      )
      .then((response) => {
        console.log(response.data.investorsPresentations);
        setReportsTable(response.data.investorsPresentations);
      });
  }, []);

  useEffect(() => {}, []);
  return (
    <div className="investor-representation">
      <div className="container-fluid">
        <div className="investors-presentation-data-header">
          <form className="htd__form row">
            <div className="type d-flex flex-row col-12">
              <p className="my-auto">Type</p>
              <div className="select-dropdown">
                <select>
                  <option value="1">Earnings Call</option>
                  <option value="2">Annual Report </option>
                  <option value="3">Corporate Guides</option>
                  <option value="4">Minutes Of General</option>
                  <option value="5">Articles Of general </option>
                  <option value="6"> Others</option>
                  <option value="7"> Real Estate Valuation Report</option>
                  <option value="8">Investor Presentations </option>
                  <option value="9"> Earnings Call Script</option>
                </select>
              </div>
            </div>

            <div className="date d-flex flex-row   col-12">
              <p className="my-auto">Date-From</p>

              <input type="date" />

              <p className="my-auto">Date-To</p>

              <input type="date" />
            </div>
          </form>
        </div>

        <div className="reports-table w-100">
          <table className="w-100">
            <tr>
              <td>Date</td>
              <td>Report</td>
              <td>Report Type</td>
              <td>Downloads</td>
            </tr>

            {reportsTable.map((report) => {
              const d = new Date(report.createdOn);
              console.log(d);

              return (
                <tr>
                  <td>{`${d.getDate()}/${
                    d.getMonth() + 1
                  }/${d.getFullYear()}`}</td>
                  <td>{report.descriptionEn}</td>
                  <td>{report.typeNameEn}</td>
                  <td className="d-flex flex-row justify-content-start">
                    <div>
                      <a href={report.attachLinkUrlEn}>
                        <img src={require("../../assets/En-pdf.png")} />
                      </a>
                    </div>

                    <div>
                      <a href={report.attachLinkUrlAr}>
                        <img src={require("../../assets/Ar-pdf.png")} />
                      </a>
                    </div>
                  </td>
                </tr>
              );
            })}
          </table>
        </div>
      </div>
    </div>
  );
}

export default Investorrepresentation;
