import React, { useEffect, useState } from "react";
import "./business-segments-tabs.css";
import axios from "axios";
import Api from "../../Api/Api";
import {
  BsFillArrowRightCircleFill,
  BsFillArrowLeftCircleFill,
} from "react-icons/bs";

function Businesssegmentstabs() {
  const [salesData, setSalesData] = useState([]);
  const [costOfSalesData, setCostOfSalesData] = useState([]);
  const [netIncomeData, setNetIncomeData] = useState([]);
  const [totalAssetsData, setTotalAssetsData] = useState([]);
  const [totalLiabilitiesData, setTotalLiabilitiesData] = useState([]);

  useEffect(() => {
    Api.getAccessToken();
    axios
      .get(
        "https://Data.argaam.com/api/v1/json/ir-api/financial-statements/en?fiscalPeriodType=year",
        {
          headers: { authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )
      .then((response) => {
        setSalesData(response.data.tabs[0].fields[0].values);
        setCostOfSalesData(response.data.tabs[0].fields[1].values);
        setNetIncomeData(response.data.tabs[0].fields[23].values);
        setTotalAssetsData(response.data.tabs[1].fields[36].values);
        setTotalLiabilitiesData(response.data.tabs[1].fields[48].values);

        console.log(response.data);
      });
  }, []);
  return (
    <div className="business-segments container">
      <div className="header-of-tabs">
        <div className="tabs-container">
          <span className="tabs"> ANNUAL</span>
          <span className="tabs"> QUARTER</span>
          <span className="tabs"> ANNUAL</span>
        </div>

        <div className="currency-container text-end">
          <span>Currency : </span>

          <span>
            <button className="text-btn-danger">USD</button>
          </span>
          <span>
            <button className="text-btn-danger">RIYAL</button>
          </span>

          <div className="my-5">
            <table className="center">
              <tbody>
                <tr>
                  <td colSpan={5}>
                    <BsFillArrowLeftCircleFill />
                  </td>
                  {salesData.map((v) => {
                    return (
                      <td
                        colSpan={3}
                        style={{ backgroundColor: "white", fontSize: "12px" }}
                      >
                        {v.fiscalPeriod}
                      </td>
                    );
                  })}
                  <td>
                    <BsFillArrowRightCircleFill />
                  </td>
                </tr>
                <tr>
                  <td colSpan={5}></td>
                  {salesData.map((v) => {
                    return (
                      <td colSpan={3} style={{ fontSize: "12px" }}>
                        Million Riyal
                      </td>
                    );
                  })}
                  <td></td>
                </tr>

                <tr style={{ backgroundColor: "#f5f5f5", height: "100px" }}>
                  <td colSpan={3}></td>
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>{" "}
                  <td colSpan={3}></td> <td colSpan={3}></td>
                  <td colSpan={3}></td> <td colSpan={3}></td>
                  <td colSpan={3}></td> <td colSpan={3}></td>
                  <td colSpan={3}></td> <td colSpan={3}></td>
                </tr>

                <tr>
                  <td colSpan={5} style={{ fontSize: "12px" }}>
                    Sales Income
                  </td>

                  {salesData.map((v) => {
                    return (
                      <td colSpan={3} style={{ fontSize: "12px" }}>
                        {v.value}
                      </td>
                    );
                  })}
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="tabs-salesData"></div>
    </div>
  );
}

export default Businesssegmentstabs;
