import React, { useEffect, useState } from "react";
import "./business-segments-tabs.css";
import axios from "axios";
import Api from "../../Api/Api";
import {
  BsFillArrowRightCircleFill,
  BsFillArrowLeftCircleFill,
} from "react-icons/bs";

function Businesssegmentstabs() {
  const [data, setData] = useState([]);
  useEffect(() => {
    Api.getAccessToken();
    axios
      .get(
        "https://data.argaam.com/api/v1/json/ir-api/financial-statements/en?fiscalPeriodType=year",
        {
          headers: { authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      )
      .then((response) => {
        setData(response.data.tabs[0].fields);
      });
  }, []);
  return (
    <div className="business-segments container">
      <div className="header-of-tabs">
        <div className="tabs-container">
          <span className="tabs"> ANNUAL</span>
          <span className="tabs"> QUARTER</span>
          <span className="tabs"> ANNUAL</span>
        </div>

        <div className="currency-container text-end">
          <span>Currency : </span>

          <span>
            <button className="text-btn-danger">USD</button>
          </span>
          <span>
            <button className="text-btn-danger">RIYAL</button>
          </span>

          <div className="my-5">
            <table className="center">
              <tbody>
                <tr>
                  <td>
                    <BsFillArrowLeftCircleFill />
                  </td>
                  {data[0].values.map((v) => {
                    return <td>{v.fiscalPeriod}</td>;
                  })}
                  <td>
                    <BsFillArrowRightCircleFill />
                  </td>
                </tr>
                <tr>
                  <td></td>
                  <td>Million Riyal</td>
                  <td>Million Riyal</td>
                  <td>Million Riyal</td>
                  <td>Million Riyal</td>
                  <td>Million Riyal</td>
                  <td></td>
                </tr>

                <tr>
                  <td>Sales Income</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="tabs-data"></div>
    </div>
  );
}

export default Businesssegmentstabs;
